ion&#34;&gt;.&lt;/span&gt;tshark_do&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;pcapfile&lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt;arg&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;pcapfile&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; fieldvalue&lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt;arg&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;fieldvalue&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; filterfield&lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt;arg&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;filterfield&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;158&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    kbparser&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;formatkbdata&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;159&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    kbparser&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;jiemi&lt;span class=&#34;token punctuation&#34;&gt;(&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;)&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;得到键盘输入后解 base85&lt;/p&gt;
