&lt;figure class=&#34;highlight js&#34;&gt;&lt;figcaption data-lang=&#34;JavaScript&#34;&gt;&lt;/figcaption&gt;&lt;table&gt;&lt;tr&gt;&lt;td data-num=&#34;1&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;git clone git&lt;span class=&#34;token operator&#34;&gt;:&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;/&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;/&lt;/span&gt;github&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;com&lt;span class=&#34;token operator&#34;&gt;/&lt;/span&gt;oleavr&lt;span class=&#34;token operator&#34;&gt;/&lt;/span&gt;frida&lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;agent&lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;example&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;git&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;2&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt; cd frida&lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;agent&lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;example&lt;span class=&#34;token operator&#34;&gt;/&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;3&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt; npm install&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;4&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt; frida &lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;&lt;span class=&#34;token constant&#34;&gt;U&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;f com&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;example&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;android &lt;span class=&#34;token operator&#34;&gt;--&lt;/span&gt;no&lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;pause &lt;span class=&#34;token operator&#34;&gt;-&lt;/span&gt;l _agent&lt;span class=&#34;token punctuation&#34;&gt;.&lt;/span&gt;js 这里我直接VScode了&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;h3 id=&#34;frida常用命令&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#frida常用命令&#34;&gt;#&lt;/a&gt; frida 常用命令&lt;/h3&gt;
&lt;pre&gt;&lt;code&gt;frida-ls-devices # 列出所有设备

frida-ps -U # 列出usb设备的进程

frida-ps -Ua # 列出正在跑的应用

frida-ps -Uai # 列出所有安装的应用

frida-ps -D xxxxx # 连接指定的设备，设备id可以从第一条命令获得

# 启动应用并注入脚本 后面会提到这两者的区别
frida -U -l exploit.js -f com.xxx.yyy

# 应用已经在运行，注入脚本
frida -U -l exploit.js &amp;quot;XXXX&amp;quot;
&lt;/code&gt;&lt;/pre&gt;
